import React, { useState } from 'react';
import L from 'leaflet';
import { useMap } from 'react-leaflet';
import './MapUI.scss';

function MapUI() {
  const [showFilterMenu, setShowFilterMenu] = useState(false);
  const map = useMap();

  const handleZoomChange = (newZoomLevel) => {
    map.setZoom(newZoomLevel);
  };

  return (
    <div className="map-ui">
      <div className="top-left-controls">
        <div className="zoom-control">
          {/* Placeholder for custom Zoom UI */}
          Zoom
        </div>
      </div>

      <div className="top-center-controls">
        <div className="search-container">
          <input type="text" placeholder="Search location" />
          <button>Search</button>
        </div>
        <button className="property-type-switch">Rent/Sale</button>
      </div>

      <div className="top-right-controls">
        <button className="filter-toggle" onClick={() => setShowFilterMenu(!showFilterMenu)}>
          Filters
        </button>
      </div>

      <div className="bottom-right-controls">
        {/* Placeholder for additional functional buttons */}
        Additional
      </div>

      {showFilterMenu && (
        <div className="filter-menu">
          {/* Placeholder for filter menu contents */}
          Filter Options
        </div>
      )}

      <div className="bottom-right-controls">
            <button className="misc-button">Misc</button>
      </div>

      <div className="zoom-control">
        <input 
          type="range" 
          min="0" 
          max="18" 
          defaultValue={map.getZoom()} 
          onChange={(e) => handleZoomChange(e.target.value)}
        />
      </div>

      {showFilterMenu && (
        <div className="filter-menu">
          {/* Filter menu contents */}
        </div>
      )}
    </div>
  );
}

export default MapUI;
